import React, { useEffect, useState } from 'react'
import { Image, Text, TextInput, TouchableOpacity, ScrollView , View , StyleSheet} from 'react-native';
import styles from './styles';
// import * as firebase from 'firebase/app';
import firebase from 'firebase/app'
import 'firebase/auth';
import 'firebase/database';




export default function TeamAnalystRegistrationScreen({navigation}){

    const [fullName, setFullName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [teamAdminInvite, setInviteCode] = useState('')

    
    const onSignUpPress = () => {
        //  Full name Validation
        if(fullName == "")
        {
            alert("Please enter your full name.")
            return
        }

        //  Email Validation
        if(email == "")
        {
            alert("Please enter your email.")
            return
        }

          //  Email Validation
          if(teamAdminInvite == "")
          {
              alert("Please enter your teamAdminInvite code.")
              return
          }

        //  Password confirmation Check
        if (password !== confirmPassword) {
            alert("Passwords don't match.")
            return
        }
        //Set UserType to TA
        if (password !== confirmPassword) {
            alert("Passwords don't match.")
            return
        }



//Check if the team invite is completely unique in the users list
const checkIFTeamExists = firebase.database().ref("teams").orderByChild('teamAdminInvite').equalTo(data.teamAdminInvite).once('value' , snapshot =>  {
    if (snapshot.exists()){
      alert('This team invite code cannot be found ');
    }
    else
    {

        firebase
        .auth()
        .createUserWithEmailAndPassword(this.state.email, this.state.password)
        .then((response) => {
            const uid = response.uid;
            const userType = "TeamAnalyst";
            const data2 = {
                id: uid,
                email: this.state.email,
                fullName: this.state.fullName,
                userType:   userType,
                teamCreated: 0,
                teamplayerlimit:this.state.teamplayerlimit,
                teamgamelimit:this.state.teamgamelimit,
                teaminvitecode:this.state.teaminvitecode
            
                
            };


            const usersRef = firebase.database().ref('users/' + uid)
                 usersRef
                     .set(data2)
                          .then(() => {
                                    this.setState({
                                        isLoading: true,
                                    })
                                
                                    this.props.navigation.navigate('Login')
                                })
                                .catch((error) => {
                                    alert(error)
                                });
                        })
                        .catch((error) => {
                            alert(error)
                    });
    }

});
  





















      


        navigation.navigate('Login')
    }

    return (
        <ScrollView>
                <Text style={styles.footerText}> Please enter your full name:</Text>
                <TextInput
                    style={styles.input}
                    placeholderTextColor="#aaaaaa"
                    placeholder='Please enter your full name'
                    onChangeText={(text) => setFullName(text)}
                    value={fullName}
                    underlineColorAndroid="transparent"
                    autoCapitalize="none"
                />

                <Text style={styles.footerText}> Please enter your email:</Text>
                <TextInput
                    style={styles.input}
                    placeholderTextColor="#aaaaaa"
                    placeholder='Please enter your email'
                    onChangeText={(text) => setEmail(text)}
                    value={email}
                    underlineColorAndroid="transparent"
                    autoCapitalize="none"
                />

                <Text style={styles.footerText}> Please enter your password:</Text>
                <TextInput
                    style={styles.input}
                    placeholderTextColor="#aaaaaa"
                    secureTextEntry
                    placeholder='Password'
                    onChangeText={(text) => setPassword(text)}
                    value={password}
                    underlineColorAndroid="transparent"
                    autoCapitalize="none"
                />
                 <Text style={styles.footerText}> Please confirm your password:</Text>
                <TextInput
                    style={styles.input}
                    placeholderTextColor="#aaaaaa"
                    secureTextEntry
                    placeholder='Confirm Password'
                    onChangeText={(text) => setConfirmPassword(text)}
                    value={confirmPassword}
                    underlineColorAndroid="transparent"
                    autoCapitalize="none"
                />

                <Text style={styles.footerText}>Please enter you invite code.This can be obtained by your  Head Team Analyst - this will be in the team record:</Text>
                <TextInput
                    style={styles.input}
                    placeholderTextColor="#aaaaaa"
                    placeholder='Please enter your invite code'
                    onChangeText={(text) => setInviteCode(text)}
                    value={teamAdminInvite}
                    underlineColorAndroid="transparent"
                    autoCapitalize="none"
                />

                <View style={styles.footerView}>
                    <TouchableOpacity style={styles.button} onPress={() => onSignUpPress()}>
                    <Text style={styles.buttonTitle}>Sign up!</Text>
                    </TouchableOpacity>
                </View>

        </ScrollView>
    )
}
