                                                                <View 
                                                                    style={{
                                                                    position: 'absolute',
                                                                    left: data.x,
                                                                    top: data.y,
                                                                    backgroundColor:'#242424',
                                                                    width: 10,
                                                                    height: 10,
                                                                    borderRadius: 50
                                                                    }}>
                                                                </View> 
                                                        
                                                            )
                                                        })} 
                            
                                                        </ScrollView>
            
                                                    </ScrollView>
                                                    </ScrollView>

                                                );
                                            }
            
                                    
            
                                        }
                                        else
                                        {
            
                                            if(screenWidth < 600 || screenHeight < 376)
                                            {
                                                alert('Warning - This device is too small to display the full image. Please swipe left to view the field location or use a larger device screen size.');
                                        
                                                FieldWithPlots = (
                                                   
                                                <ScrollView>
                                                <ScrollView horizontal={true} style={stylesViewGame.imagePlotContainer}>
                                                    <TouchableOpacity style={stylesViewGame.button} onPress = {this.GoToMainMenu}>
                                                        <Text style={stylesViewGame.Text}>Back to Main Menu</Text>
                                                    </TouchableOpacity>
                
                                                    <TouchableOpacity style={stylesViewGame.button} onPress = {this.GoToViewGameMenu}>
                                                        <Text style={stylesViewGame.Text}>Back to View Game Menu</Text>
                                                    </TouchableOpacity>
                                                    
                                                </ScrollView>
                                               
                                                   
                                                    <ScrollView horizontal={true} style={stylesViewGame.imagePlotContainer}>
                                                    <ScrollView>
                                                    
                                                    
                                                            
                                                        
                                                    <Image style ={stylesViewGame.imageSoccerMobile} 
                                                                    source={require('./SoccerField.png')}/> 
                                                                    {this.state.tempEventFieldLocations.map((data) => {
                                                                        return (
                                                                        <View 
                                                                            style={{
                                                                        
                                                                            position: 'absolute',
                                                                            left: data.x,
                                                                            top: data.y,
                                                                            backgroundColor:'#242424',
                                                                            width: 10,
                                                                            height: 10,
                                                                            borderRadius: 50
                                                                            }}>
                                                                        </View> 
                                                            
                                                                        )
                                                                        })}        
                                                           
            
                                                         </ScrollView>
                                                    </ScrollView>
                                                    
                                                    </ScrollView>
                                                 
                                            
                                                );
            
                                            }
            
                                        
                                            FieldWithPlots = (
            
                                                <ScrollView>
                                                <ScrollView horizontal={true} style={stylesViewGame.imagePlotContainer}>
                                                    
                                                    <TouchableOpacity style={stylesViewGame.button} onPress = {this.GoToMainMenu}>
                                                        <Text style={stylesViewGame.Text}>Back to Main Menu</Text>
                                                    </TouchableOpacity>
                
                                                    <TouchableOpacity style={stylesViewGame.button} onPress = {this.GoToViewGameMenu}>
                                                        <Text style={stylesViewGame.Text}>Back to View Game Menu</Text>
                                                    </TouchableOpacity>
                
                                                </ScrollView>

                                                <ScrollView horizontal={true} style={stylesViewGame.imagePlotContainer}>
                                                <ScrollView>
                                                            
                                                        <Image style ={stylesViewGame.imagePxSoccer}                              
                                                            source={require('./SoccerField.png')}/>
                                                            {this.state.tempEventFieldLocations.map((data) => {
                                                                return (
                                                                <View 
                                                                    style={{
                                                                    position: 'absolute',
                                                                    left: data.x,
                                                                    top: data.y,
                                                                    backgroundColor:'#242424',
                                                                    width: 10,
                                                                    height: 10,
                                                                    borderRadius: 50
                                                                    }}>
                                                                </View> 
                                                        
                                                            )
                                                        })} 
                            
                                                        </ScrollView>
                                                    </ScrollView>

                                                    </ScrollView>

                                            );
                                        }
            
            
            
                                }
                }



            return (
 

                <ScrollView contentContainerStyle={{ flexGrow: 1, justifyContent: 'center', backgroundColor: '#252626', alignItems: "center", fontSize: 20}}>

                    <View>
                        {ReviewSelection}  
                    </View>
                        
                    <View>
                        {FieldWithPlots}
                    </View>

                    
                    <View>
                        {ReviewGameStats}
                    </View>

                    
                    <View>
                        {ReviewPlayerStats}
                    </View>

                    
                    <View>
                        {PlayerStats}
                    </View>

                </ScrollView>
        
                      

                       


            )

        }
}


